import React from "react";
import { useDispatch } from "react-redux";
import {remove }from "../redux/Slices/cartSlice";
import { toast } from "react-hot-toast";
import {FcDeleteDatabase} from "react-icons/fc"



const CartItem = ({item,itemindex}) => {
const dispatch=useDispatch();

const removeFromCart=()=>{
  dispatch(remove(item.id));
  toast.success("Item removed from Cart");
}

  return (
  
<div className="h-[800px] w-[600px] border-black my-5 pr-5 py-10 flex items-center flex-row justify-center">
  <div >
    <div className="h-[200px] w-[200px]">
    <img src={item.image} className="h-full w-full"></img>
    </div>
    <div className= "flex justify-between gap-12 items-center w-full mt-5">
      <h1 className="text-green-600 font-semibold">{item.title}</h1>
      <h1 className="text-green-600 font-semibold">{item.description}</h1>
    </div>
    <div>
      <p className="text-green-600 font-semibold">{item.price}</p>
      <div onClick={removeFromCart}>
      <FcDeleteDatabase/>
      </div>

      </div>
    </div>
  </div>

  )
};

export default CartItem;
